+@Composable
+fun PriceChange(
+ priceChange: DisplayableNumber, modifier: Modifier = Modifier
+) {
+ val contentColor = if (priceChange.value < 0.0) {
+ MaterialTheme.colorScheme.onErrorContainer
+ } else {
+ Color.Green
+ }
+ val backgroundColor = if (priceChange.value < 0.0) {
+ MaterialTheme.colorScheme.errorContainer
+ } else {
+ greenBackground
+ }
+
+ Row(
+ modifier = modifier
+ .clip(RoundedCornerShape(100f))
+ .background(backgroundColor)
+ .padding(horizontal = 4.dp),
+ verticalAlignment = Alignment.CenterVertically
+ ) {
+ Icon(
+ imageVector = if (priceChange.value < 0.0) {
+ Icons.Default.KeyboardArrowDown
+ } else {
+ Icons.Default.KeyboardArrowUp
+ },
+ contentDescription = null,
+ modifier = Modifier
+ .size(20.dp),
+ tint = contentColor
+ )
+ Text(
+ text = "${priceChange.formatted} %",
+ color = contentColor,
+ fontSize = 14.sp,
+ fontWeight = FontWeight.Medium
+ )
+ }
+}
